describe("Testing did-jwk methods", () => {
  it("Should create a did from jwk", async () => {
    const jwk = await JWK.asKey(KEY);

    const did = new DidJwk(jwk);
    assert.isTrue(JWK_DID_REGEX.test(did.getDidUri()));
  });

  it("Should parse JWK DID", async () => {
    DidJwk.fromUri(DID);
  });

  it("Should generate the valid DID document", () => {
    assert.doesNotThrow(async () => {
      const didDocument = await (await DidJwk.fromUri(DID)).getDidDocument();
      assert.notEqual(didDocument, null);
    });
  });

  it("Should create did from x5c", async () => {
    const didJwk = await DidJwk.fromX5c(X5C);
    await didJwk.verify();
  });

  // X5C public key error
  it("Should throw invalid public key error", async () => {
    const jwk = await JWK.asKey(KEY_2);
    jwk["x5c"] = X5C;

    const didJwk = new DidJwk(jwk);
    //  const didJwk: any = await DidJwk.fromX5c(X5C);

    //    const jwk = await JWK.asKey(KEY_2);

    try {
      await didJwk.verify();
    } catch (err) {
      assert.equal(
        err.message,
        "JWK does not match the key in the certificate!"
      );
    }
  });

  // x5c chain error
});

