describe("Testing cert chains", () => {
  it("Should convert cert to x5c", async () => {
    const rootCertPem = fs.readFileSync(
      "./src/test/certs/root/root.crt",
      "utf-8"
    );
    const certPem = fs.readFileSync(
      "./src/test/certs/cert1/cert1.crt",
      "utf-8"
    );

    const x5c = Certs.certPemsToX5c([certPem, rootCertPem]);

    expect(x5c).to.eql([
      "",
      "",
    ]);
  });

  it("x5c verification should pass", () => {
    const x5c = [
      "",
      "",
    ];

    const verificationResult = Certs.verifyX5c(x5c);
    expect(verificationResult.domainName).to.eql("example.org");
  });

  it("x5c verification should fail", () => {
    const x5c = [
      "",
      "",
    ];

    try {
      Certs.verifyX5c(x5c);
    } catch (err) {
      expect(err.error).to.equal("forge.pki.BadCertificate");
      expect(err.message).to.equal("Certificate signature is invalid.");
    }
  });

  it("Should throw invalid domain error", () => {
    const x5c = [
      "",
      "",
    ];

    try {
      Certs.verifyX5c(x5c);
    } catch (err) {
      expect(err.message).equal("Invalid domain name!");
    }
  });
});
